        ctrl.pp.remainingBatchSize -= ctrl.batchSize;
        ctrl.pp.batchSize = ctrl.pp.remainingBatchSize;

        if (ctrl.pp.remainingBatchSize == 0) {
          ctrl.pp.lotNumber = -1;
        }
       
